'use client';

import { useState, useEffect } from 'react';
import Navbar from '@/components/Navbar';
import ProductCard from '@/components/ProductCard';
import { Heart, Loader2 } from 'lucide-react';
import api from '@/lib/api';
import { motion, AnimatePresence } from 'framer-motion';
import Link from 'next/link';

export default function FavoritesPage() {
    const [products, setProducts] = useState<any[]>([]);
    const [loading, setLoading] = useState(true);

    const fetchFavorites = async () => {
        setLoading(true);
        try {
            // First get profile for favorite IDs
            const profileRes = await api.get('/auth/profile');
            const favoriteIds = profileRes.data.favorites;

            if (favoriteIds && favoriteIds.length > 0) {
                // Fetch all products and filter locally for simplicity in JSON mode
                const productsRes = await api.get('/products', { params: { limit: 100 } });
                const favProducts = productsRes.data.products.filter((p: any) => favoriteIds.includes(p._id));
                setProducts(favProducts);
            } else {
                setProducts([]);
            }
        } catch (err) {
            console.error('Failed to fetch favorites', err);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchFavorites();
    }, []);

    return (
        <div className="min-h-screen bg-gray-50">
            <Navbar />

            <main className="max-w-7xl mx-auto px-6 py-12">
                <header className="mb-12">
                    <h1 className="text-4xl font-black text-gray-900 mb-2">My Favorites</h1>
                    <p className="text-gray-500">Items you've liked from our marketplace</p>
                </header>

                <AnimatePresence mode="wait">
                    {loading ? (
                        <div className="flex items-center justify-center py-32" key="loader">
                            <Loader2 className="w-8 h-8 text-primary animate-spin" />
                        </div>
                    ) : products.length > 0 ? (
                        <motion.div
                            initial={{ opacity: 0 }}
                            animate={{ opacity: 1 }}
                            exit={{ opacity: 0 }}
                            className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6"
                            key="grid"
                        >
                            {products.map((product) => (
                                <ProductCard
                                    key={product._id}
                                    id={product._id}
                                    title={product.title}
                                    price={product.price}
                                    image={product.image}
                                    category={product.category}
                                />
                            ))}
                        </motion.div>
                    ) : (
                        <div className="text-center py-32 bg-white rounded-3xl border border-dashed border-gray-200" key="empty">
                            <div className="w-16 h-16 bg-gray-50 rounded-full flex items-center justify-center mx-auto mb-4">
                                <Heart className="w-8 h-8 text-gray-300" />
                            </div>
                            <p className="text-lg text-gray-500 font-medium">You haven't favorited any items yet.</p>
                            <Link href="/products" className="mt-4 inline-block px-6 py-3 bg-primary text-white rounded-xl font-bold hover:bg-primary/90 transition-soft">
                                Browse Products
                            </Link>
                        </div>
                    )}
                </AnimatePresence>
            </main>
        </div>
    );
}

'use client';

import { motion, useMotionValue, useSpring, useTransform } from 'framer-motion';
import Navbar from '@/components/Navbar';
import ProductCard from '@/components/ProductCard';
import { ChevronRight, Loader2, Sparkles } from 'lucide-react';
import { useEffect, useState } from 'react';
import api from '@/lib/api';
import Link from 'next/link';

export default function Home() {
  const [products, setProducts] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);

  // Magnetic Button Logic
  const x = useMotionValue(0);
  const y = useMotionValue(0);
  const springConfig = { damping: 15, stiffness: 150 };
  const mouseX = useSpring(x, springConfig);
  const mouseY = useSpring(y, springConfig);

  const handleMouseMove = (event: React.MouseEvent) => {
    const rect = event.currentTarget.getBoundingClientRect();
    const centerX = rect.left + rect.width / 2;
    const centerY = rect.top + rect.height / 2;
    x.set(event.clientX - centerX);
    y.set(event.clientY - centerY);
  };

  const handleMouseLeave = () => {
    x.set(0);
    y.set(0);
  };

  useEffect(() => {
    setLoading(true);
    api.get('/products?limit=4')
      .then(res => setProducts(res.data.products))
      .catch(err => console.error('Failed to fetch hero products', err))
      .finally(() => setLoading(false));
  }, []);

  return (
    <div className="min-h-screen bg-white">
      <Navbar />

      {/* Hero Section */}
      <section className="relative overflow-hidden pt-20 pb-28 lg:pt-32 lg:pb-40">
        {/* Animated Background Overlay */}
        <motion.div
          animate={{
            scale: [1, 1.1, 1],
            rotate: [0, 5, 0],
          }}
          transition={{ duration: 20, repeat: Infinity, ease: "linear" }}
          className="absolute top-0 right-0 -z-10 w-full h-full bg-gradient-to-br from-primary/10 via-white to-transparent blur-3xl opacity-40"
        />

        <div className="max-w-7xl mx-auto px-6 relative z-10">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-12 items-center">
            <motion.div
              initial={{ opacity: 0, x: -50 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.8, ease: "easeOut" }}
              className="max-w-2xl"
            >
              <div className="inline-flex items-center gap-2 px-4 py-2 bg-primary/5 rounded-full border border-primary/10 mb-8">
                <Sparkles className="w-4 h-4 text-primary animate-pulse" />
                <span className="text-xs font-bold text-primary uppercase tracking-widest">New Season Drop</span>
              </div>

              <h1 className="text-4xl xs:text-6xl lg:text-8xl font-black tracking-tight text-gray-900 mb-8 leading-[1.1]">
                Elevate Your <span className="text-transparent bg-clip-text bg-gradient-to-r from-primary to-blue-600">Setup</span>
              </h1>

              <p className="text-xl text-gray-600 mb-12 leading-relaxed max-w-xl">
                Experience the perfect blend of aesthetics and performance with our curated mechanical gear and desk accessories.
              </p>

              <div className="flex flex-wrap gap-6">
                <motion.div
                  onMouseMove={handleMouseMove}
                  onMouseLeave={handleMouseLeave}
                  style={{ x: mouseX, y: mouseY }}
                  className="relative group"
                >
                  <Link href="/products" className="relative z-10 block px-10 py-5 bg-gray-900 text-white rounded-2xl font-bold transition-all duration-300 group-hover:bg-primary group-hover:scale-105 shadow-2xl shadow-gray-900/20 active:scale-95">
                    Shop Now
                  </Link>
                  <div className="absolute inset-0 bg-primary/20 blur-2xl rounded-2xl scale-0 group-hover:scale-125 transition-transform duration-500 -z-10" />
                </motion.div>

                <Link href="/products?category=Desk Setup" className="px-10 py-5 bg-white text-gray-900 border-2 border-gray-100 rounded-2xl font-bold hover:bg-gray-50 hover:border-gray-200 transition-all flex items-center gap-2">
                  View Setup <ChevronRight className="w-5 h-5" />
                </Link>
              </div>
            </motion.div>

            <motion.div
              initial={{ opacity: 0, scale: 0.8, rotate: 10 }}
              animate={{ opacity: 1, scale: 1, rotate: 0 }}
              transition={{ duration: 1, ease: "easeOut", delay: 0.2 }}
              className="hidden lg:block relative"
            >
              <div className="relative z-10 rounded-[3rem] overflow-hidden shadow-2xl skew-y-1">
                <img
                  src="https://images.unsplash.com/photo-1547394765-185e1e68f34e?auto=format&fit=crop&q=80&w=1200"
                  alt="Premium Workspace"
                  className="w-full aspect-[4/5] object-cover"
                />
              </div>
              <div className="absolute -bottom-10 -right-10 w-64 h-64 bg-primary/20 blur-3xl rounded-full -z-10 animate-pulse" />
              <div className="absolute -top-10 -left-10 w-64 h-64 bg-blue-400/10 blur-3xl rounded-full -z-10" />
            </motion.div>
          </div>
        </div>
      </section>

      {/* Featured Products */}
      <section className="py-24 px-6 bg-gray-50/30">
        <div className="max-w-7xl mx-auto">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            className="flex items-center justify-between mb-16"
          >
            <div>
              <h2 className="text-4xl font-black text-gray-900 mb-2">Editor's Picks</h2>
              <p className="text-gray-500 text-lg">Finest essentials for your creative flow</p>
            </div>
            <Link href="/products" className="group flex items-center gap-2 text-primary font-black hover:gap-3 transition-all">
              Go to Store <ChevronRight className="w-5 h-5" />
            </Link>
          </motion.div>

          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-8">
            {!loading ? products.map((product, idx) => (
              <ProductCard
                key={product._id}
                id={product._id}
                title={product.title}
                price={product.price}
                image={product.image}
                category={product.category}
                index={idx}
              />
            )) : (
              [1, 2, 3, 4].map((i) => (
                <div key={i} className="aspect-square bg-gray-100 animate-pulse rounded-[2rem]" />
              ))
            )}
          </div>
        </div>
      </section>

      {/* Category Grid Section */}
      <section className="py-24 px-6">
        <div className="max-w-7xl mx-auto grid grid-cols-1 md:grid-cols-3 gap-8">
          <CategoryCard title="Mechanical" tag="Keyboards" image="https://images.unsplash.com/photo-1618384881928-111e03d4044a?auto=format&fit=crop&q=80&w=800" />
          <CategoryCard title="Studio" tag="Audio" image="https://images.unsplash.com/photo-1546435770-a3e426bf472b?auto=format&fit=crop&q=80&w=800" />
          <CategoryCard title="Desk" tag="Accessories" image="https://images.unsplash.com/photo-1625842268584-8f3bf9ff16a0?auto=format&fit=crop&q=80&w=800" />
        </div>
      </section>
    </div>
  );
}

function CategoryCard({ title, tag, image }: { title: string; tag: string; image: string }) {
  return (
    <Link href={`/products?category=${tag}`}>
      <motion.div
        whileHover={{ y: -10 }}
        className="relative h-96 rounded-[2.5rem] overflow-hidden group cursor-pointer shadow-lg hover:shadow-2xl transition-all duration-500"
      >
        <img
          src={image}
          alt={title}
          className="w-full h-full object-cover grayscale-[0.2] group-hover:grayscale-0 group-hover:scale-110 transition-all duration-700"
          onError={(e) => {
            (e.target as HTMLImageElement).src = 'https://images.unsplash.com/photo-1587829741301-dc798b83dadc?auto=format&fit=crop&q=80&w=800';
          }}
        />
        <div className="absolute inset-0 bg-gradient-to-t from-black/80 via-black/20 to-transparent flex flex-col justify-end p-8">
          <span className="text-primary font-bold text-xs uppercase tracking-[0.2em] mb-2">{tag}</span>
          <h3 className="text-3xl font-black text-white group-hover:translate-x-2 transition-transform">{title}</h3>
        </div>
      </motion.div>
    </Link>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { useParams, useRouter } from 'next/navigation';
import Navbar from '@/components/Navbar';
import { Heart, ShoppingCart, ArrowLeft, ShieldCheck, Truck, RefreshCcw, Loader2 } from 'lucide-react';
import api from '@/lib/api';
import { motion } from 'framer-motion';
import productsData from '@/lib/products.json';

export async function generateStaticParams() {
    return productsData.map((product) => ({
        id: product._id,
    }));
}

export default function ProductDetailPage() {
    const { id } = useParams();
    const router = useRouter();
    const [product, setProduct] = useState<any>(null);
    const [loading, setLoading] = useState(true);
    const [isFavorite, setIsFavorite] = useState(false);

    useEffect(() => {
        api.get(`/products/${id}`)
            .then(res => setProduct(res.data))
            .catch(() => router.push('/products'))
            .finally(() => setLoading(false));
    }, [id]);

    const toggleFavorite = async () => {
        try {
            const res = await api.post(`/products/${id}/favorite`);
            setIsFavorite(res.data.includes(id));
        } catch (err) {
            router.push('/auth/login');
        }
    };

    if (loading) return (
        <div className="min-h-screen">
            <Navbar />
            <div className="flex items-center justify-center py-64">
                <Loader2 className="w-10 h-10 text-primary animate-spin" />
            </div>
        </div>
    );

    return (
        <div className="min-h-screen bg-white">
            <Navbar />

            <main className="max-w-7xl mx-auto px-6 py-12">
                <button onClick={() => router.back()} className="flex items-center gap-2 text-gray-500 font-bold hover:text-primary transition-colors mb-8">
                    <ArrowLeft className="w-5 h-5" /> Back
                </button>

                <div className="grid grid-cols-1 lg:grid-cols-2 gap-16">
                    {/* Image Gallery */}
                    <motion.div
                        initial={{ opacity: 0, x: -20 }}
                        animate={{ opacity: 1, x: 0 }}
                        className="relative aspect-square rounded-[2rem] overflow-hidden bg-gray-50 border border-gray-100"
                    >
                        <img src={product.image} alt={product.title} className="w-full h-full object-cover" />
                    </motion.div>

                    {/* Product Info */}
                    <motion.div
                        initial={{ opacity: 0, x: 20 }}
                        animate={{ opacity: 1, x: 0 }}
                        className="flex flex-col"
                    >
                        <div className="mb-8">
                            <span className="px-4 py-1.5 bg-primary/10 text-primary text-xs font-black uppercase tracking-widest rounded-full">
                                {product.category}
                            </span>
                            <h1 className="text-4xl lg:text-5xl font-extrabold text-gray-900 mt-6 mb-4">{product.title}</h1>
                            <p className="text-3xl font-bold text-gray-900">₹{product.price}</p>
                        </div>

                        <p className="text-lg text-gray-600 leading-relaxed mb-10">
                            {product.description}
                        </p>

                        <div className="flex gap-4 mb-12">
                            <button className="flex-1 py-5 bg-primary text-white rounded-2xl font-bold hover:bg-primary/90 transition-soft shadow-xl shadow-primary/25 flex items-center justify-center gap-3">
                                <ShoppingCart className="w-6 h-6" /> Add to Cart
                            </button>
                            <button
                                onClick={toggleFavorite}
                                className={`p-5 rounded-2xl transition-soft border-2 ${isFavorite ? "bg-red-50 text-red-500 border-red-100" : "bg-white text-gray-400 border-gray-100 hover:text-red-500"
                                    }`}
                            >
                                <Heart className={`w-7 h-7 ${isFavorite && "fill-current"}`} />
                            </button>
                        </div>

                        {/* Perks */}
                        <div className="grid grid-cols-3 gap-4 p-8 bg-gray-50 rounded-3xl">
                            <div className="text-center">
                                <Truck className="w-6 h-6 text-primary mx-auto mb-2" />
                                <p className="text-xs font-bold text-gray-900">Free Shipping</p>
                            </div>
                            <div className="text-center">
                                <ShieldCheck className="w-6 h-6 text-primary mx-auto mb-2" />
                                <p className="text-xs font-bold text-gray-900">2 Year Warranty</p>
                            </div>
                            <div className="text-center">
                                <RefreshCcw className="w-6 h-6 text-primary mx-auto mb-2" />
                                <p className="text-xs font-bold text-gray-900">30 Day Returns</p>
                            </div>
                        </div>
                    </motion.div>
                </div>
            </main>
        </div>
    );
}

'use client';

import { useState, useEffect, Suspense } from 'react';
import { useSearchParams } from 'next/navigation';
import Navbar from '@/components/Navbar';
import ProductCard from '@/components/ProductCard';
import { Search, Loader2 } from 'lucide-react';
import api from '@/lib/api';
import { motion, AnimatePresence } from 'framer-motion';

function ProductsContent() {
    const searchParams = useSearchParams();
    const [products, setProducts] = useState<any[]>([]);
    const [loading, setLoading] = useState(true);
    // Sync state with URL params
    const [searchTerm, setSearchTerm] = useState(searchParams.get('search') || '');
    const [category, setCategory] = useState(searchParams.get('category') || 'All');
