'use client';

import Link from 'next/link';
import { ShoppingCart, Heart, User, Search, LogOut } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';
import { useEffect, useState } from 'react';
import { useRouter } from 'next/navigation';

export default function Navbar() {
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [searchQuery, setSearchQuery] = useState('');
    const [isMobileSearchOpen, setIsMobileSearchOpen] = useState(false);
    const router = useRouter();

    const checkAuth = () => {
        setIsLoggedIn(!!localStorage.getItem('token'));
    };

    useEffect(() => {
        checkAuth();
        // Listen for storage changes (for multiple tabs) or custom 'auth-change' events
        window.addEventListener('storage', checkAuth);
        window.addEventListener('auth-change', checkAuth);

        return () => {
            window.removeEventListener('storage', checkAuth);
            window.removeEventListener('auth-change', checkAuth);
        };
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('token');
        setIsLoggedIn(false);
        router.push('/');
        window.dispatchEvent(new Event('auth-change'));
    };

    const handleSearch = (e: React.FormEvent) => {
        e.preventDefault();
        if (searchQuery.trim()) {
            router.push(`/products?search=${encodeURIComponent(searchQuery)}`);
        }
    };

    return (
        <nav className="sticky top-0 z-50 glass border-b border-gray-100 px-4 md:px-6 py-4">
            <div className="max-w-7xl mx-auto flex items-center justify-between gap-4">
                <Link href="/" className="text-xl md:text-2xl font-bold text-primary flex items-center gap-2 shrink-0">
                    <motion.div
                        initial={{ rotate: -10 }}
                        animate={{ rotate: 0 }}
                        whileHover={{ scale: 1.1 }}
                    >
                        🛒
                    </motion.div>
                    <span className={isMobileSearchOpen ? 'hidden xs:block' : 'block'}>MicroMarket</span>
                </Link>

                {/* Search Bar (Desktop) */}
                <form onSubmit={handleSearch} className="hidden md:flex flex-1 max-w-md relative">
                    <Search className="absolute left-4 top-1/2 -translate-y-1/2 w-4 h-4 text-gray-400" />
                    <input
                        type="text"
                        value={searchQuery}
                        onChange={(e) => setSearchQuery(e.target.value)}
                        placeholder="Search setup gear..."
                        className="w-full pl-10 pr-4 py-2 bg-gray-50 border border-transparent rounded-xl focus:bg-white focus:border-primary focus:ring-4 focus:ring-primary/10 transition-soft outline-none text-sm"
                    />
                </form>

                {/* Search Bar (Mobile Inline) */}
                <AnimatePresence>
                    {isMobileSearchOpen && (
                        <motion.form
                            initial={{ opacity: 0, scale: 0.95 }}
                            animate={{ opacity: 1, scale: 1 }}
                            exit={{ opacity: 0, scale: 0.95 }}
                            onSubmit={handleSearch}
                            className="flex md:hidden flex-1 relative"
                        >
                            <input
                                autoFocus
                                type="text"
                                value={searchQuery}
                                onChange={(e) => setSearchQuery(e.target.value)}
                                placeholder="Search..."
                                className="w-full pl-4 pr-4 py-2 bg-gray-100 border-none rounded-xl focus:bg-white focus:ring-2 focus:ring-primary transition-soft outline-none text-sm"
                            />
                        </motion.form>
                    )}
                </AnimatePresence>

                <div className="flex items-center gap-1 sm:gap-4">
                    <button
                        onClick={() => setIsMobileSearchOpen(!isMobileSearchOpen)}
                        className="md:hidden p-2 hover:bg-gray-100 rounded-full transition-colors"
                    >
                        <Search className="w-5 h-5 text-gray-600" />
                    </button>

                    <Link href="/products" className="hidden sm:block text-sm font-medium text-gray-600 hover:text-primary transition-colors">Browse</Link>

                    <Link href="/favorites" className="p-2 hover:bg-gray-100 rounded-full transition-colors relative">
                        <Heart className="w-5 h-5 text-gray-600" />
                    </Link>

                    {isLoggedIn ? (
                        <div className="flex items-center gap-2 sm:gap-3">
                            <div className="w-8 h-8 rounded-full bg-primary/10 flex items-center justify-center text-primary font-bold text-xs">
                                U
                            </div>
                            <button
                                onClick={handleLogout}
                                className="p-2 hover:bg-red-50 hover:text-red-500 rounded-full transition-colors"
                                title="Logout"
                            >
                                <LogOut className="w-5 h-5" />
                            </button>
                        </div>
                    ) : (
                        <Link href="/auth/login" className="flex items-center gap-2 px-3 sm:px-4 py-2 bg-primary text-white rounded-lg hover:bg-primary/90 transition-soft text-sm font-medium">
                            <User className="w-4 h-4" />
                            <span className="hidden xs:block">Sign In</span>
                        </Link>
                    )}
                </div>
            </div>
        </nav>
    );
}

'use client';

import Link from 'next/link';
import { Heart } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';
import { useState, useEffect } from 'react';
import api from '@/lib/api';

interface ProductCardProps {
    id: string;
    title: string;
    price: number;
    image: string;
    category: string;
    index?: number;
}

const particleVariants = {
    initial: { scale: 0, opacity: 1 },
    animate: (i: number) => ({
        scale: [0, 1.2, 0],
        opacity: [1, 1, 0],
        x: Math.cos(i * 45) * 40,
        y: Math.sin(i * 45) * 40,
        transition: { duration: 0.6, ease: "easeOut" }
    })
};

export default function ProductCard({ id, title, price, image, category, index = 0 }: ProductCardProps) {
    const [isFavorite, setIsFavorite] = useState(false);
    const [loading, setLoading] = useState(false);
    const [showParticles, setShowParticles] = useState(false);

    useEffect(() => {
        const checkFavorite = async () => {
            const token = localStorage.getItem('token');
            if (!token) return;
            try {
                const res = await api.get('/auth/profile');
                if (res.data.favorites?.includes(id)) {
                    setIsFavorite(true);
                }
            } catch (err) {
                console.error('Failed to check favorite status');
            }
        };
        checkFavorite();
    }, [id]);

    const toggleFavorite = async (e: React.MouseEvent) => {
        e.preventDefault();
        e.stopPropagation();

        const token = localStorage.getItem('token');
        if (!token) {
            alert('Please sign in to favorite products');
            return;
        }

        setLoading(true);
        if (!isFavorite) setShowParticles(true);

        try {
            const res = await api.post(`/products/${id}/favorite`);
            const currentlyFavorite = res.data.includes(id);
            setIsFavorite(currentlyFavorite);

            if (!currentlyFavorite) setShowParticles(false);
            else setTimeout(() => setShowParticles(false), 600);
        } catch (err) {
            console.error('Failed to toggle favorite');
            setShowParticles(false);
        } finally {
            setLoading(false);
        }
    };

    return (
        <motion.div
            layout
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{
                duration: 0.5,
                delay: index * 0.1,
                ease: [0.21, 1.11, 0.81, 0.99]
            }}
            whileHover={{
                y: -10,
                rotateX: 2,
                rotateY: 2,
                scale: 1.02,
                transition: { duration: 0.3 }
            }}
            className="group bg-white rounded-3xl border border-gray-100 overflow-hidden shadow-sm hover:shadow-2xl hover:shadow-primary/10 transition-all duration-500 perspective-1000"
        >
            <Link href={`/products/${id}`}>
                <div className="relative aspect-square overflow-hidden bg-gray-50">
                    <motion.img
                        src={image || 'https://images.unsplash.com/photo-1587829741301-dc798b83dadc?auto=format&fit=crop&q=80&w=800'}
                        alt={title}
                        className="w-full h-full object-cover"
                        whileHover={{ scale: 1.15 }}
                        transition={{ duration: 0.8, ease: "easeOut" }}
                        onError={(e) => {
                            (e.target as HTMLImageElement).src = 'https://images.unsplash.com/photo-1587829741301-dc798b83dadc?auto=format&fit=crop&q=80&w=800';
                        }}
                    />

                    <div className="absolute top-4 left-4">
                        <span className="px-3 py-1.5 bg-white/95 backdrop-blur-md rounded-full text-[10px] font-black uppercase tracking-wider text-primary shadow-sm border border-white/20">
                            {category}
                        </span>
                    </div>

                    <div className="absolute top-4 right-4 flex items-center justify-center">
                        <AnimatePresence>
                            {showParticles && Array.from({ length: 8 }).map((_, i) => (
                                <motion.div
                                    key={i}
                                    custom={i}
                                    variants={particleVariants}
                                    initial="initial"
                                    animate="animate"
                                    className="absolute w-2 h-2 bg-red-500 rounded-full z-10 pointer-events-none"
                                />
                            ))}
                        </AnimatePresence>

                        <motion.button
                            onClick={toggleFavorite}
                            disabled={loading}
                            whileHover={{ scale: 1.1 }}
                            whileTap={{ scale: 0.9, rotate: -15 }}
                            className={`relative p-3 rounded-full backdrop-blur-md transition-all duration-300 z-20 ${isFavorite
                                    ? 'bg-red-500 text-white shadow-lg shadow-red-200'
                                    : 'bg-white/90 text-gray-400 hover:text-red-500 hover:bg-white'
                                }`}
                        >
                            <Heart className={`w-4 h-4 ${isFavorite ? 'fill-current' : ''}`} />
                        </motion.button>
                    </div>
                </div>

                <div className="p-6">
                    <motion.h3
                        className="font-bold text-gray-900 group-hover:text-primary transition-colors line-clamp-1 mb-2 text-lg"
                    >
                        {title}
                    </motion.h3>
                    <div className="flex items-center justify-between">
                        <span className="text-2xl font-black text-gray-900">₹{price.toLocaleString('en-IN')}</span>
                        <div className="flex items-center gap-2 group/btn">
                            <span className="text-xs font-bold text-gray-400 group-hover/btn:text-primary transition-colors">Details</span>
                            <motion.div
                                className="w-8 h-8 rounded-full bg-gray-50 flex items-center justify-center group-hover/btn:bg-primary group-hover/btn:text-white transition-colors"
                                whileHover={{ x: 5 }}
                            >
                                <span className="text-lg leading-none mt-[-2px]">→</span>
                            </motion.div>
                        </div>
                    </div>
                </div>
            </Link>
        </motion.div>
    );
}

import productsData from './products.json';

// Mock API to replace Axel/Backend for static hosting (GitHub Pages)
// This uses localStorage to simulate database persistence for users and favorites.

const DELAY = 500;

const wait = (ms: number) => new Promise(resolve => setTimeout(resolve, ms));

// Helper to get users from localStorage
const getLocalUsers = () => {
    if (typeof window === 'undefined') return [];
    const users = localStorage.getItem('mock_users');
    return users ? JSON.parse(users) : [];
};

const saveLocalUsers = (users: any[]) => {
    localStorage.setItem('mock_users', JSON.stringify(users));
};

const getCurrentUser = () => {
    if (typeof window === 'undefined') return null;
    const token = localStorage.getItem('token');
    if (!token) return null;
    const users = getLocalUsers();
    return users.find((u: any) => u.id === token) || null;
};

const api = {
    get: async (url: string) => {
        await wait(DELAY);

        // GET /products
        if (url.startsWith('/products')) {
            const urlObj = new URL(url, 'http://localhost');
            const search = urlObj.searchParams.get('search')?.toLowerCase();
            const category = urlObj.searchParams.get('category');
            const limit = parseInt(urlObj.searchParams.get('limit') || '100');
            const page = parseInt(urlObj.searchParams.get('page') || '1');

            let filtered = [...productsData];

            if (search) {
                filtered = filtered.filter(p =>
                    p.title.toLowerCase().includes(search) ||
                    p.description.toLowerCase().includes(search)
                );
            }

            if (category && category !== 'All') {
                filtered = filtered.filter(p => p.category === category);
            }

            const start = (page - 1) * limit;
            const paginated = filtered.slice(start, start + limit);

            return {
                data: {
                    products: paginated,
                    totalPages: Math.ceil(filtered.length / limit),
                    currentPage: page,
                    totalProducts: filtered.length
                }
            };
        }

        // GET /products/:id
        if (url.match(/\/products\/(\w+)/)) {
            const id = url.split('/').pop();
            const product = productsData.find(p => p._id === id);
            if (!product) throw { response: { status: 404, data: { message: 'Not found' } } };
            return { data: product };
        }

        // GET /auth/profile
        if (url === '/auth/profile') {
            const user = getCurrentUser();
            if (!user) throw { response: { status: 401, data: { message: 'Unauthorized' } } };
            return { data: user };
        }

        throw new Error(`Mock API: Route ${url} not implemented`);
    },

    post: async (url: string, data?: any) => {
        await wait(DELAY);

        // POST /auth/register
        if (url === '/auth/register') {
            const users = getLocalUsers();
            if (users.find((u: any) => u.email === data.email)) {
                throw { response: { status: 400, data: { message: 'User already exists' } } };
            }
            const newUser = {
                id: Math.random().toString(36).substr(2, 9),
                ...data,
                favorites: []
            };
            users.push(newUser);
            saveLocalUsers(users);
            return { data: { token: newUser.id } };
        }

        // POST /auth/login
        if (url === '/auth/login') {
            const users = getLocalUsers();
            const user = users.find((u: any) => u.email === data.email && u.password === data.password);
            if (!user) {
                throw { response: { status: 400, data: { message: 'Invalid credentials' } } };
            }
            return { data: { token: user.id } };
        }

        // POST /products/:id/favorite
        if (url.match(/\/products\/(\w+)\/favorite/)) {
            const id = url.split('/')[2];
            const currentUser = getCurrentUser();
            if (!currentUser) throw { response: { status: 401, data: { message: 'Unauthorized' } } };

            const users = getLocalUsers();
            const userIndex = users.findIndex((u: any) => u.id === currentUser.id);

            if (!users[userIndex].favorites) users[userIndex].favorites = [];

            const favIndex = users[userIndex].favorites.indexOf(id);
            if (favIndex > -1) {
                users[userIndex].favorites.splice(favIndex, 1);
            } else {
                users[userIndex].favorites.push(id);
            }

            saveLocalUsers(users);
            return { data: users[userIndex].favorites };
        }

        throw new Error(`Mock API: Route ${url} not implemented`);
    }
};

export default api;
